// src/NetworkGraph.js
import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';

const NetworkGraph = () => {
  const svgRef = useRef();

  useEffect(() => {
    const width = 600;
    const height = 400;

    const data = {
      nodes: [
        { id: 'A' },
        { id: 'B' },
        { id: 'C' },
        { id: 'D' },
      ],
      links: [
        { source: 'A', target: 'B' },
        { source: 'A', target: 'C' },
        { source: 'B', target: 'D' },
      ],
    };

    const svg = d3.select(svgRef.current)
      .attr('width', width)
      .attr('height', height);

    // Force simulation
    const simulation = d3.forceSimulation(data.nodes)
      .force('link', d3.forceLink(data.links).id(d => d.id).distance(100))
      .force('charge', d3.forceManyBody().strength(-200))
      .force('center', d3.forceCenter(width / 2, height / 2));

    // Create links
    const link = svg.selectAll('.link')
      .data(data.links)
      .enter().append('line')
      .attr('class', 'link')
      .attr('stroke', '#000')
      .attr('stroke-width', 2);

    // Create nodes
    const node = svg.selectAll('.node')
      .data(data.nodes)
      .enter().append('circle')
      .attr('class', 'node')
      .attr('r', 10)
      .attr('fill', 'blue')
      .call(d3.drag()
        .on('start', dragStart)
        .on('drag', drag)
        .on('end', dragEnd));

    // Add labels to nodes
    svg.selectAll('.label')
      .data(data.nodes)
      .enter().append('text')
      .attr('class', 'label')
      .attr('x', 12)
      .attr('y', 4)
      .text(d => d.id);

    // Update positions on each tick of the simulation
    simulation.on('tick', () => {
      link
        .attr('x1', d => d.source.x)
        .attr('y1', d => d.source.y)
        .attr('x2', d => d.target.x)
        .attr('y2', d => d.target.y);

      node
        .attr('cx', d => d.x)
        .attr('cy', d => d.y);
    });

    // Drag event functions
    function dragStart(event, d) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function drag(event, d) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragEnd(event, d) {
      if (!event.active) simulation.alphaTarget(0);
      d.fx = null;
      d.fy = null;
    }
  }, []);


  return (
    <div>
      <svg ref={svgRef}></svg>
    </div>
  );
};

export default NetworkGraph;
