// src/NetworkGraph.js
import React, { useRef, useEffect, useState } from 'react';
import * as d3 from 'd3';

const NetworkGraph = () => {
  const svgRef = useRef();
  const [numNodes, setNumNodes] = useState(1); 
  const [links, setLinks] = useState([]); 
  const [newStart, setNewStart] = useState("");
  const [newEnd, setNewEnd] = useState("");

  // console.log(numNodes); 
  const handleNumNodeChange = (event) => {
    setNumNodes(event.target.value); 
    setLinks([])
  }

  const handleStartChange = (event) => {
    console.log("changing start to...")
    console.log(event.target.value)
    setNewStart(event.target.value); 
  }

  const handleEndChange = (event) => {
    console.log("changing end to...")
    console.log(event.target.value)
    setNewEnd(event.target.value); 
  }

  const handleLinksChange = (event) => {
    console.log("adding link between...")
    console.log(newStart)
    console.log(newEnd)
    addNewLink(newStart, newEnd); 
  }

  const addNewLink = (startNode, endNode) => {
    console.log("adding new link...")
    console.log(startNode)
    console.log(endNode)
    const updatedLinks = [...links, { source: startNode, target: endNode }];
    setLinks(updatedLinks); 
  }

  useEffect(() => {
    const width = 600;
    const height = 400;

    const nodes = []; 

    if (numNodes <= 26) {
        for (let i = 0; i < numNodes; i++) {
            nodes.push({id: String.fromCharCode(65 + i)});
        }
    }


    // const links = []; 

    // if (numNodes <= 26) {
    //     for (let i = 0; i < numNodes; i++) {
    //         for (let j =i + 1; j < numNodes; j++) {
    //             links.push({ source: nodes[i].id, target: nodes[j].id}); 
    //         }
    //     }
    // }

    // Initialize canvas to hold the graph
    const svg = d3
      .select(svgRef.current)
      .attr('width', width)
      .attr('height', height);

    svg.selectAll("*").remove();

    // Define arrow marker
    svg
      .append('defs')
      .append('marker')
      .attr('id', 'arrow')
      .attr('viewBox', '0 0 10 10')
      .attr('refX', 25)
      .attr('refY', 5)
      .attr('markerWidth', 6)
      .attr('markerHeight', 6)
      .attr('orient', 'auto')
      .append('path')
      .attr('d', 'M 0 0 L 10 5 L 0 10 z')
      .attr('fill', '#7f8c8d');

    const simulation = d3
      .forceSimulation(nodes)
      .force('center', d3.forceCenter(width / 2, height / 2))
      .force('charge', d3.forceManyBody())
      .force(
        'link',
        d3.forceLink(links).id((d) => d.id).distance(100)
      )
      .on('tick', ticked);

    const linkSelection = svg
      .selectAll('.link')
      .data(links)
      .enter()
      .append('line')
      .attr('class', 'link')
      .attr('stroke', 'black')
      .attr('stroke-width', 1)
      .attr('marker-end', 'url(#arrow)');

    const nodeSelection = svg
      .selectAll('circle')
      .data(nodes)
      .enter()
      .append('circle')
      .attr('r', 10)
      .attr('fill', 'steelblue')
      .call(
        d3
          .drag()
          .on('start', dragStart)
          .on('drag', drag)
          .on('end', dragEnd)
      );

      const labelSelection = svg
      .selectAll('text')
      .data(nodes)
      .enter()
      .append('text')
      .text((d) => d.id) // Display the node ID
      .attr('font-size', 10)
      .attr('fill', "white")
      .attr('text-anchor', 'middle') // Center-align the text horizontally
      .attr('dy', 4); // Position the text slightly above the circle

    function ticked() {
      nodeSelection
        .attr('cx', (d) => d.x)
        .attr('cy', (d) => d.y);

      linkSelection
        .attr('x1', (d) => d.source.x)
        .attr('y1', (d) => d.source.y)
        .attr('x2', (d) => d.target.x)
        .attr('y2', (d) => d.target.y);

    labelSelection
    .attr('x', (d) => d.x)
    .attr('y', (d) => d.y); // Align the text exactly with the node center
    }

    function dragStart(event, d) {
        console.log('drag start');
      simulation.alphaTarget(0.5).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function drag(event, d) {
        console.log('dragging');
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragEnd(event, d) {
        console.log('drag end');
      simulation.alphaTarget(0.1);
      d.fx = null;
      d.fy = null;
    }
  }, [numNodes, links]);


const elements = []; 
if (numNodes <= 26) {
    for (let i = 0; i < numNodes; i++) {
        const value = String.fromCharCode(65 + i); 
        elements.push(<option key = {value}>{value}</option>);
    }
}

  return (
    <div>
        <div><span>Select number of nodes: </span><input type = "number" value = {numNodes} onChange={handleNumNodeChange} max="26" 
  min="1"></input> <button> Save </button></div>
        <div>
            <span>Add start of new link: </span><select value = {newStart} onChange={handleStartChange}>{elements}</select>
            <span>Add end of new link: </span><select value = {newEnd} onChange={handleEndChange}>{elements}</select>
            <button onClick = {handleLinksChange}> Add New Link </button>
        </div>
        <div>
            <svg ref={svgRef}></svg>
        </div>
    </div>

  );
};

export default NetworkGraph;
