// src/NetworkGraph.js
import React, { useRef, useEffect, useState } from 'react';
import * as d3 from 'd3';

const NetworkGraph = () => {
  const svgRef = useRef();
  const [numNodes, setNumNodes] = useState(1); 

  console.log(numNodes); 
  const handleNumNodeChange = (event) => {
    setNumNodes(event.target.value); 
  }

  useEffect(() => {
    const width = 600;
    const height = 400;

    const nodes = []; 

    for (let i = 0; i < numNodes; i++) {
        nodes.push({id: String.fromCharCode(65 + i)});
    }

    const links = []; 

    for (let i = 0; i < numNodes; i++) {
        for (let j =i + 1; j < numNodes; j++) {
            links.push({ source: nodes[i].id, target: nodes[j].id}); 
        }
    }

    const data = {
        nodes: [
            { id: 'A' },
            { id: 'B' },
            { id: 'C' },
            { id: 'D' },
            { id: 'E' },
          ],
          links: [
            { source: 'A', target: 'B' },
            { source: 'A', target: 'C' },
            { source: 'A', target: 'D' },
            { source: 'A', target: 'E' },
            { source: 'B', target: 'C' },
            { source: 'B', target: 'D' },
            { source: 'B', target: 'E' },
            { source: 'C', target: 'D' },
            { source: 'C', target: 'E' },
            { source: 'D', target: 'E' },

          ],
    };

    // Initialize canvas to hold the graph
    const svg = d3
      .select(svgRef.current)
      .attr('width', width)
      .attr('height', height);

    svg.selectAll("*").remove();

    const simulation = d3
      .forceSimulation(nodes)
      .force('center', d3.forceCenter(width / 2, height / 2))
      .force('charge', d3.forceManyBody())
      .force(
        'link',
        d3.forceLink(links).id((d) => d.id).distance(100)
      )
      .on('tick', ticked);

    const linkSelection = svg
      .selectAll('line')
      .data(links)
      .enter()
      .append('line')
      .attr('stroke', 'black')
      .attr('stroke-width', 1);

    const nodeSelection = svg
      .selectAll('circle')
      .data(nodes)
      .enter()
      .append('circle')
      .attr('r', 10)
      .attr('fill', 'steelblue')
      .call(
        d3
          .drag()
          .on('start', dragStart)
          .on('drag', drag)
          .on('end', dragEnd)
      );

      const labelSelection = svg
      .selectAll('text')
      .data(nodes)
      .enter()
      .append('text')
      .text((d) => d.id) // Display the node ID
      .attr('font-size', 12)
      .attr('fill', "white")
      .attr('text-anchor', 'middle') // Center-align the text horizontally
      .attr('dy', 4); // Position the text slightly above the circle

    function ticked() {
      nodeSelection
        .attr('cx', (d) => d.x)
        .attr('cy', (d) => d.y);

      linkSelection
        .attr('x1', (d) => d.source.x)
        .attr('y1', (d) => d.source.y)
        .attr('x2', (d) => d.target.x)
        .attr('y2', (d) => d.target.y);

    labelSelection
    .attr('x', (d) => d.x)
    .attr('y', (d) => d.y); // Align the text exactly with the node center
    }

    function dragStart(event, d) {
        console.log('drag start');
      simulation.alphaTarget(0.5).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function drag(event, d) {
        console.log('dragging');
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragEnd(event, d) {
        console.log('drag end');
      simulation.alphaTarget(0.1);
      d.fx = null;
      d.fy = null;
    }
  }, [numNodes]);

  return (
    <div>
        <div><span>Select number of nodes: </span><input type = "number" value = {numNodes} onChange={handleNumNodeChange} max="26" 
  min="1"></input> <button> Submit </button></div>
        <div>
      <svg ref={svgRef}></svg>
    </div>
    </div>

  );
};

export default NetworkGraph;
