// src/NetworkGraph.js
import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';

const NetworkGraph = () => {
  const svgRef = useRef();

  useEffect(() => {
    const width = 600;
    const height = 400;

    const data = {
      nodes: [
        { id: 'A' },
        { id: 'B' },
        { id: 'C' },
        { id: 'D' },
      ],
      links: [
        { source: 'A', target: 'B' },
        { source: 'B', target: 'C' },
        { source: 'C', target: 'D' },
        { source: 'D', target: 'A' },
      ],
    };

    // Initialize canvas to hold the graph
    const svg = d3
      .select(svgRef.current)
      .attr('width', width)
      .attr('height', height);

    const simulation = d3
      .forceSimulation(data.nodes)
      .force('center', d3.forceCenter(width / 2, height / 2))
      .force('charge', d3.forceManyBody())
      .force(
        'link',
        d3.forceLink(data.links).id((d) => d.id).distance(100)
      )
      .on('tick', ticked);

    const linkSelection = svg
      .selectAll('line')
      .data(data.links)
      .enter()
      .append('line')
      .attr('stroke', 'black')
      .attr('stroke-width', 1);

    const nodeSelection = svg
      .selectAll('circle')
      .data(data.nodes)
      .enter()
      .append('circle')
      .attr('r', 10)
      .attr('fill', 'steelblue')
      .call(
        d3
          .drag()
          .on('start', dragStart)
          .on('drag', drag)
          .on('end', dragEnd)
      );

    function ticked() {
      nodeSelection
        .attr('cx', (d) => d.x)
        .attr('cy', (d) => d.y);

      linkSelection
        .attr('x1', (d) => d.source.x)
        .attr('y1', (d) => d.source.y)
        .attr('x2', (d) => d.target.x)
        .attr('y2', (d) => d.target.y);
    }

    function dragStart(event, d) {
      simulation.alphaTarget(0.5).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function drag(event, d) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragEnd(event, d) {
      simulation.alphaTarget(0.1);
      d.fx = null;
      d.fy = null;
    }
  }, []);

  return (
    <div>
      <svg ref={svgRef}></svg>
    </div>
  );
};

export default NetworkGraph;
