// src/NetworkGraph.js
import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';

const NetworkGraph = () => {
  const svgRef = useRef();

  useEffect(() => {
    const width = 600;
    const height = 400;

    // graph data (nodes and edges)
    const data = {
        nodes: [
            { id: 'A' },
            { id: 'B' },
            { id: 'C' },
            { id: 'D' },
          ],
          links: [
            { source: 'A', target: 'B' },
            { source: 'B', target: 'C' },
            { source: 'C', target: 'D' },
            { source: 'D', target: 'A' },
          ],
      };

      // initialize canvas to hold the graph
      const svg = d3.select(svgRef.current)
        .attr('width', 600)
        .attr('height', 400);

     const simulation = d3.forceSimulation(data.nodes);
     const nodeSelection = svg
      .selectAll("circle")
      .data(nodes)
      .enter()
      .append("circle")
      .attr("r", d => d.size)
      .attr("fill", d => d.color)
      .call(
        d3
        .drag()
        .on("start", dragStart)
        .on("drag", drag)
        .on("end", dragEnd));

    node.attr('cx', (d,i) => (i+1)*100).attr('cy', height/2); 

     simulation
        .force("center", d3.forceCenter(width / 2, height / 2))
        .force("nodes", d3.forceManyBody())
        .force(
            "links",
        d3
        .forceLink(data.links)
        .id(d => d.id)
        .distance(100)
        )
  .on("tick", ticked);

function ticked() {
  // console.log(simulation.alpha());

  nodeSelection.attr("cx", d => d.x).attr("cy", d => d.y);
}

    const link = svg.selectAll('.link')
      .data(data.links)
      .enter().append('line')
      .attr('class', 'link')
      .attr('stroke', '#000')
      .attr('stroke-width', 2); 

    const node = svg.selectAll('.node')
      .data(data.nodes)
      .enter().append('circle')
      .attr('class', 'node')
      .attr('r', 10)
      .attr('fill', 'blue'); 

    const label = svg.selectAll('.label')
     .data(data.nodes)
     .enter().append('text')
     .attr('class', 'label')
     .attr('x', (d, i) => (i + 1) * 100 + 12)
     .attr('y', height / 2 + 4)
     .text(d => d.id);

    svg.selectAll('.label')
      .data(data.nodes)
      .enter().append('text')
      .attr('class', 'label')
      .attr('x', (d, i) => (i+1)*100 + 12)
      .attr('y', height/2 + 4)
      .text(d => d.id);

    simulation.on('tick', () => {
        link
          .attr('x1', d => d.source.x)
          .attr('y1', d => d.source.y)
          .attr('x2', d => d.target.x)
          .attr('y2', d => d.target.y); 
        node
          .attr('cx', d => d.x)
          .attr('cy', d => d.y); 
        label
          .attr('x', d => d.x + 12)  // Offset label next to the node
          .attr('y', d => d.y + 4);   // Offset label slightly below the node
          nodeSelection.attr("cx", d => d.x).attr("cy", d => d.y);
    })


      function dragStart(d) {
        // console.log('drag start');
        simulation.alphaTarget(0.5).restart();
        d.fx = d.x;
        d.fy = d.y;
      }

      function drag(d) {
        // console.log('dragging');
        // simulation.alpha(0.5).restart()
        d.fx = d3.event.x;
        d.fy = d3.event.y;
      }

      function dragEnd(d) {
        // console.log('drag end');
        simulation.alphaTarget(0);
        d.fx = null;
        d.fy = null;
      }

  }, [])

  return <div>
    <svg ref = {svgRef}></svg
    ></div>
};

export default NetworkGraph;
